/*!

=========================================================
* Argon Dashboard React - v1.1.0
=========================================================

* Product Page: https://www.creative-tim.com/product/argon-dashboard-react
* Copyright 2019 Creative Tim (https://www.creative-tim.com)
* Licensed under MIT (https://github.com/creativetimofficial/argon-dashboard-react/blob/master/LICENSE.md)

* Coded by Creative Tim

=========================================================

* The above copyright notice and this permission notice shall be included in all copies or substantial portions of the Software.

*/
import React from "react";
// node.js library that concatenates classes (strings)
import classnames from "classnames";
// javascipt plugin for creating charts
import Chart from "chart.js";
// react plugin used to create charts
import { Line, Bar } from "react-chartjs-2";
// reactstrap components
import {
  Button,
  Card,
  CardHeader,
  CardBody,
  NavItem,
  NavLink,
  Nav,
  Progress,
  Table,
  Container,
  Row,
  Col,
  CardTitle
} from "reactstrap";

// core components
import {
  chartOptions,
  parseOptions,
  chartExample1,
  chartExample2
} from "variables/charts.js";

// import firebase
import firebase from 'firebase/app';

import Header from "components/Headers/Header.js";

class Index extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      activeNav: 1,
      chartExample1Data: "data1",
      userID: "Unknown",
      firstName: "Unknown",
      lastName: "Unknown",
      age: "Unknown",
      gender: "Unknown",
      workoutCompletion: 0,
      workoutStillNeed: 0,
      lastWorkout: "Unknown"
    };
    if (window.Chart) {
      parseOptions(Chart, chartOptions());
    }
  }

  toggleNavs = (e, index) => {
    e.preventDefault();
    this.setState({
      activeNav: index,
      chartExample1Data:
        this.state.chartExample1Data === "data1" ? "data2" : "data1"
    });
  };

  componentDidMount() {
    // Update the firstName
    let firstNameRef = firebase.databse().ref(this.state.userID + '/firstName');
    firstNameRef.on('value', (snapshot) => {
      let value = snapshot.val();
      this.setState({firstName: value});
    });

    // Update the lastName
    let lastNameRef = firebase.databse().ref(this.state.userID + '/lastName');
    lastNameRef.on('value', (snapshot) => {
      let value = snapshot.val();
      this.setState({lastName: value});
    });

    // Update the age
    let ageRef = firebase.databse().ref(this.state.userID + '/age');
    ageRef.on('value', (snapshot) => {
      let value = snapshot.val();
      this.setState({age: value});
    });

    // Update the gender
    let genderRef = firebase.databse().ref(this.state.userID + '/gender');
    genderRef.on('value', (snapshot) => {
      let value = snapshot.val();
      this.setState({gender: value});
    });

    // Update the workoutCompletion
    let workoutCompletionRef = firebase.databse().ref(this.state.userID + '/workoutCompletion');
    workoutCompletionRef.on('value', (snapshot) => {
      let value = snapshot.val();
      this.setState({workoutCompletion: value});
    });

    // Update the workoutStillNeed
    let workoutStillNeedRef = firebase.databse().ref(this.state.userID + '/workoutStillNeed');
    workoutStillNeedRef.on('value', (snapshot) => {
      let value = snapshot.val();
      this.setState({workoutStillNeed: value});
    });

    // Update the lastWorkout
    let lastWorkoutRef = firebase.databse().ref(this.state.userID + '/lastWorkout');
    lastWorkoutRef.on('value', (snapshot) => {
      let value = snapshot.val();
      this.setState({lastWorkout: value});
    });
  }

  // Cancel all the listeners
  componentWillUnmount() {
    firstNameRef.off();
    lastNameRef.off();
    ageRef.off();
    genderRef.off();
    workoutCompletionRef.off();
    workoutStillNeedRef.off();
    lastWorkoutRef.off();
  }

  // Update profile
  addProfile = (firstNameDes, lastNameDes, ageDes, genderDes, workoutComDes, workoutStiDes, lastDes) => {

    let firstNameRef = firebase.database().ref(this.state.userID + '/firstname');
    firstNameRef.set(firstNameDes);

    let lastNameRef = firebase.database().ref(this.state.userID + '/lastname');
    lastNameRef.set(lastNameDes);

    let ageRef = firebase.database().ref(this.state.userID + '/age');
    ageRef.set(ageDes);

    let genderRef = firebase.database().ref(this.state.userID + '/gender');
    genderRef.set(genderDes);    

    let workoutCompletionRef = firebase.database().ref(this.state.userID + '/workoutCompletion');
    workoutCompletionRef.set(workoutComDes);    

    let workoutStillNeedRef = firebase.databse().ref(this.state.userID + '/workoutStillNeed');
    workoutStillNeedRef.set(workoutStiDes);

    let lastWorkoutRef = firebase.databse().ref(this.state.userID + '/lastWorkout');
    lastWorkoutRef.set(lastDes);    
  }

  // replace the userID
  addUserID = (user) => {
    this.setState({userID: user.replace(/[^a-zA-Z0-9]/g, "")});
  }

  render() {
    return (
      <>
        <Header />
        {/* Page content */}
        <Container className="mt--7" fluid>
          <Row>
            <Col xl="8">
              <Row>
                <Col xl="8">
                <Card className="shadow p-3">
                  <Row>
                    <Col><CardTitle>Abs Workout</CardTitle></Col>
                    <Col><p>Progress bar</p></Col>
                    <Col><Button></Button></Col>
                  </Row>
                </Card>
                </Col>
                <Col xl="4">
                  <Button></Button>
                  <Button></Button>
                </Col>
              </Row>
              <Row className="mt-5 ml-1">
                <Button className="bigbutton"></Button>
                <Button className="bigbutton"></Button>
              </Row>
              <Row></Row>
            </Col>
            <Col className="mb-5 mb-xl-0" xl="4">
              <Card className="bg-gradient-default shadow">
                <CardHeader className="bg-transparent">
                  <Row className="align-items-center">
                    <div className="col">
                      <h6 className="text-uppercase text-light ls-1 mb-1">
                        Overview
                      </h6>
                      <h2 className="text-white mb-0">Sales value</h2>
                    </div>
                    <div className="col">
                      <Nav className="justify-content-end" pills>
                        <NavItem>
                          <NavLink
                            className={classnames("py-2 px-3", {
                              active: this.state.activeNav === 1
                            })}
                            href="#pablo"
                            onClick={e => this.toggleNavs(e, 1)}
                          >
                            <span className="d-none d-md-block">Month</span>
                            <span className="d-md-none">M</span>
                          </NavLink>
                        </NavItem>
                        <NavItem>
                        </NavItem>
                      </Nav>
                    </div>
                  </Row>
                </CardHeader>
                <CardBody>
                  {/* Chart */}
                  <div className="chart">
                    <Line
                      data={chartExample1[this.state.chartExample1Data]}
                      options={chartExample1.options}
                      getDatasetAtEvent={e => console.log(e)}
                    />
                  </div>
                </CardBody>
              </Card>
            </Col>
          </Row>
        </Container>
      </>
    );
  }
}

export default Index;
